  modelTo.create(targetModelData, options, function(err, targetModel) {
    if(!err) {
      //Refresh the cache
      apply(targetModel, self.addToCache.bind(self));
      cb && cb(err, targetModel);
    } else {
      cb && cb(err);
    }
  });
